import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';

import InputTodoItem from "./InputTodoItem"
import DisplayTodoList from "./DisplayTodoList"
import OpenAIRequest from './OpenAIRequest';
import Home from './Home';

import "./App.css";

function App() {
    // The JSX returned by the component
    return (
      
      <BrowserRouter>
        <div className="App" >
          {/* Navbar setup using react-bootstrap components */}
          <Navbar className='navbar-custom'>
            <Container>
              <Navbar.Brand className="nav-link-custom" href="/home">Task Manager</Navbar.Brand>
              <Nav className="me-auto">
                {/* Navigation links */}
                
              </Nav>
            </Container>
          </Navbar>
  
          {/* Routes setup for different pages in the application */}
          <Routes>
            {/* Route for the home page */}
            <Route path='/Home' element={<Home />}></Route>
            {/* Route for the About Us page */}
            <Route path='/aboutUs' element={<AboutUs></AboutUs>}></Route>         
          </Routes>
          
          {/* Footer component displayed on all pages */}
          <Footer className="footer"/> 
        </div>
      </BrowserRouter>
    );
  }

export default App;
