import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const OpenAIRequest = () => {
  const [userInput, setUserInput] = useState('');
  const [conversation, setConversation] = useState([]);
  const endOfMessagesRef = useRef(null);

  const scrollToBottom = () => {
    endOfMessagesRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [conversation]);

  const generateResponse = async () => {
    if (!userInput.trim()) return;
    const userMessage = userInput.trim();
    setUserInput('');
    setConversation(conversation => [...conversation, { sender: 'user', text: userMessage }]);

    try {
      const response = await axios.post(
        'https://api.openai.com/v1/engines/davinci-codex/completions',
        {
          prompt: userMessage,
          max_tokens: 100,
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${process.env.OPENAI_API_KEY}`,
          },
        }
      );

      const botResponse = response.data.choices[0].text.trim();
      setConversation(conversation => [...conversation, { sender: 'bot', text: botResponse }]);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <div className="chat-interface">
        {conversation.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
        <div ref={endOfMessagesRef}></div>
      </div>
      <input
        value={userInput}
        onChange={e => setUserInput(e.target.value)}
        placeholder="Type your message here..."
        onKeyDown={e => e.key === 'Enter' && generateResponse()}
      />
      <button onClick={generateResponse}>Send</button>
    </div>
  );
};

export default OpenAIRequest;
